import { cartConstant } from "../../constant/cartConstant";

export const cartReducer = (state = {cart: [], cartLoading: true, quantity: 0}, action:any) => {
    
    switch (action.type) {

            case cartConstant.REQUEST_CART:
                
                return { 
                    cartLoading: true, 
                    cart: null
                    }

            case cartConstant.GET_CART:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                    }

            case cartConstant.ADD_PRODUCT:
                
                return { 
                    cartLoading: true, 
                    cart: action.payload 
                    }

            case cartConstant.SUCCESS_ADD:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                    }

            case cartConstant.DELETE_PRODUCT:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                }
    
            case cartConstant.ADD_QUANTITY:
                return { 
                    cartLoading: false, 
                    quantity: action.quantity + 1
                }

            case cartConstant.DROP_QUANTITY:
                return { 
                    cartLoading: false, 
                    error: action.payload
                }
                
            default:
                return state;
        }



}
