import React from 'react';

type Props = {
    editMode: boolean,
    firstName : string,
    lastName: string
};

export const DynamicInput = (props: Props) => {
  return <div>

        <p className='text-[12px] text-gray-500 mb-2'>First Name</p>
        <h1 className='text-lg px-6 py-3 bg-blue-50 rounded-lg' style={props.editMode ? {display: 'none'} : {display: 'block'}}>{props.firstName}</h1>
          
        <input type="text" value={props.firstName} style={props.editMode ? {display: 'block'} : {display: 'none'}} className='text-lg px-6 py-3 bg-blue-50 rounded-lg w-full'/>

  </div>;
};
