type Props = {

    item: any,
    index: number
}

function PurchaseItem(props: Props) {
  return (
    <div className="flex w-full bg-gray-50 p-3 rounded-lg mb-3" key={props.index}>
      <img
        src={props.item.image}
        alt={props.item.name}
        className="object-cover h-[100px] w-[100px] rounded-lg mr-10"
      />
      <section className="text-xs flex flex-col justify-around w-full">
        <p>{props.item.name}</p>
        <section className="flex justify-between text-gray-500">
          <p>variation</p>
          <p>Quantity: {props.item.quantity}</p>
        </section>
        <p className="font-medium">
          ${(props.item.price * props.item.quantity).toFixed(2)}
        </p>
      </section>

      <button
                    className="px-5 py-1 bg-gradient-to-r from-[#2c69d1]  to-[#0abcf9] rounded-md  text-white float-right transition-transform transform hover:scale-110 absolute bottom-0 right-0 mr-5 mb-5 "
                    onClick={paymentItemHandler(order.id)}
                  >
                    Pay
          </button>
    </div>
  );
}

export default PurchaseItem
