import React from "react";
import { useDispatch, useSelector, RootStateOrAny } from "react-redux";
import { incrementQuantity, decrementQuantity } from '../services/cart/cartAction';

type Props = {
  image: string;
  id: string
  name: string;
  quantity: number;
  price: number;
  index: number;
  order: any[]
};

export const CartItem = (props: Props) => {

  const cartDetail = useSelector(
    (state: RootStateOrAny) => state.cartReducer
  );
  const { quantity, cart, id } = cartDetail;


  const dispatch = useDispatch()

  const increment = (currentQuantity: number, id: any) => {
    dispatch(incrementQuantity(currentQuantity, id))

    const cartArr = [...cart]
    const addQuantityIndex = cartArr.findIndex(item => item.id === id)
    console.log(addQuantityIndex);
    
  }

  return (
    <div className=" grid grid-cols-4 gap-5">
        <img
          src={props.image}
          alt={props.name}
          className="object-cover rounded-lg"
        />

        <section className="py-2">
          <h1 className=" ">{props.name}</h1>
          <p className="text-gray-500 mt-4 text-sm">variation</p>
        </section>

        <section className="flex justify-center items-center ">
          <button className="m-5 font-medium hover:bg-red-400 hover:text-white px-2 rounded-md" >
            {" "}
            -{" "}
          </button>
          <span className="px-4 py-1.5 border-2 rounded-lg border-gray-300">
            {props.quantity}
          </span>
          <button className="m-5 font-medium hover:bg-green-400 hover:text-white px-2 rounded-md" onClick={increment.bind(this, props.quantity, props.id)}>
            {" "}
            +{" "}
          </button>
        </section>

        <section className="flex justify-items-center items-center">
          <span className="">${(props.price * props.quantity).toFixed(2)}</span>
        </section>
      </div>

  );
};
