export const productsReducers = (state = {products: []}, action:any) => {

    switch (action.type) {
        case 'REQUEST_PRODUCTS':
            return { 
                loading: true, 
                products: []
            }

        case 'SUCCESS_REQUEST_PRODUCTS':
            return { 
                loading: false, 
                products: action.payload
            }


        case 'FAIL_REQUEST_PRODUCTS':
            return { 
                loading: false, 
                error: action.payload
            }
            
        default:
            return state;
    }
}
