import { useDispatch } from 'react-redux';
import { addToCart } from '../services/cart/cartAction';

export const CartFunction = (cart: any[], productById: any) => {

    const dispatch = useDispatch();
  
    const addItem = () => {  
      const item = cart.findIndex((item: any) => item.id === productById.id);
      console.log(item);
  
      if (item >= 0) {
        cart[item].quantity += 1;
        return dispatch(addToCart(cart));
  
      } else {
        const cartItem = { ...productById, quantity: 1 };
        console.log(cartItem);
        cart.push(cartItem);
        return dispatch(addToCart(cart));
      }
    }
}
