import React from 'react'
import { useState } from 'react'

type Props = {}

export const Purchase = (props: Props) => {

    const [currentTab, setCurrentTab] = useState<number>(0);

    const tabChangeHandler = (number: number)  => (e: React.MouseEvent) => {
        setCurrentTab(number)
    }

    const isTabActive = (number: number) => {
        if (number === currentTab) {
            return (
                'bg-blue-500'
            )
        } else {
            return 'bg-gray-300'
        }

    }


  return (
    <div className='pl-10'>
        <div className='flex items-center pt-5'>

            <section className={`h-[80px] w-[80px]  rounded-full flex justify-center items-center relative ${isTabActive(0)}`} onClick={tabChangeHandler(0)}>
                <img src="/images/payment.png" alt="payment" className='object-contain w-[50%]' />
                <h1 className='absolute bottom-[-30px] text-sm text-gray-400'>pay</h1>
            </section>

            <span className={`w-[120px] h-[1.8px] ${isTabActive(1)}`}></span>

            <section className={`h-[80px] w-[80px]  rounded-full flex justify-center items-center relative ${isTabActive(1)}`} onClick={tabChangeHandler(1)}>
                <img src="/images/preparing.png" alt="payment" className='object-contain w-[70%]' />
                <h1 className='absolute bottom-[-30px] text-sm text-gray-400'>preparing</h1>
            </section>
            
            <span className={`w-[120px] h-[1.8px] ${isTabActive(2)}`}></span>

            <section className={`h-[80px] w-[80px]  rounded-full flex justify-center items-center relative ${isTabActive(2)}`} onClick={tabChangeHandler(2)}>
                <img src="/images/delivery.png" alt="payment" className='object-contain w-[90%]' />
                <h1 className='absolute bottom-[-30px] text-sm text-gray-400'>shipping</h1>
            </section>
            
            <span className={`w-[120px] h-[1.8px] ${isTabActive(3)}`}></span>

            <section className={`h-[80px] w-[80px]  rounded-full flex justify-center items-center relative ${isTabActive(3)}`} onClick={tabChangeHandler(3)}>
                <img src="/images/complete.png" alt="payment" className='object-contain w-[70%]' />
                <h1 className='absolute bottom-[-30px] text-sm text-gray-400'>complete</h1>
            </section>
        </div>

        <div className='w-full'>
            <img src="/images/empty-cart.png" alt="" className='object-contain w-[40%] m-auto mt-20'/>
        </div>
    </div>
  )
}
