import { OrderConstant } from "../../constant/orderConstant"

export const orderReducers = (state = {order: [], success:false, loading: false}, action:any) => {

    switch (action.type) {

        case OrderConstant.CHECKOUT:
            return { 
                loading: false,
                order: action.payload
            }

        case OrderConstant.REQUEST_CREATE_ORDER:
            return { 
                loading: true, 
                success: false, 
                order: []
            }

        case OrderConstant.CREATE_ORDER:
            return { 
                loading: false, 
                success: true, 
                order: action.payload
            }


        case OrderConstant.FAIL:
            return { 
                loading: false,
                success: false,  
                error: action.payload
            }
            
        default:
            return state;
    }
}
