import React from 'react';
import { useParams } from 'react-router-dom';
import products from '../products';
import star from '../assets/star.png'

type Props = {};

const Product = (props: Props) => {

    const { id } = useParams()
    
    const product = products.find((prod) => prod._id === id )

    return product ? 
    (<div className='grid grid-cols-2 max-w-7xl m-auto px-10 gap-10 mt-10'>
        <section>
            <img src={product.image} alt='product' className=''/>
        </section>

        <section>
            <h1 className='text-2xl'>{product.name}</h1>

            <section className='flex justify-between w-[250px] mt-4'>
                <aside className='flex items-center'>
                    <p className='text-gray-500 mr-1'>{product.rating}</p>
                    <img src={star} alt='rating' className='w-4 h-4 '/>
                </aside>

                <aside>
                    <p>Ratings {product.numReviews}</p>
                </aside>

                <aside>
                    <p>{product.numReviews} Sold</p>
                </aside>
            </section>


            <p className='mt-10'>{product.description}</p>
        </section>
    </div>)

    : null
};

export default Product;
