import React, { useEffect, useState } from "react";
import { RootStateOrAny, useSelector } from "react-redux";

import { Spinner } from "../components/Spinner/Spinner";

type Props = {};

export const Cart = (props: Props) => {
  const [cart, setCart] = useState<any[]>([]);

  const userDetail = useSelector((state: RootStateOrAny) => state.loginReducer);
  const { userInfo, loading } = userDetail;

  useEffect(() => {
    if (!loading) {
      if (userInfo.cart === null) return;
      setCart(userInfo.cart);
      console.log(userInfo.cart);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [loading]);

  return (
    <div className="max-w-7xl px-10 m-auto">
      <h1 className="text-3xl font-normal mt-10 pb-5 ">My Order</h1>
      {!loading ? (
        cart !== null ? (
          cart.map((item) => {
            return (
              <div className="grid grid-cols-4 w-[70%] gap-5 mb-5">
                <img
                  src={item.image}
                  alt={item.name}
                  className="object-cover
                        rounded-lg"
                />

                <section className="py-2">
                  <h1 className=" ">{item.name}</h1>
                  {/* <p className="text-gray-500">
                    Quantity: <span className="">{item.quantity}</span>
                  </p>
                  <p className="text-gray-500">
                    Total Price:
                    <span className="">
                      ${(item.price * item.quantity).toFixed(2)}
                    </span>
                  </p> */}
                  <p className="text-gray-500 mt-4 text-sm">variation</p>
                </section>

                <section className="flex justify-center items-center ">
                    <button className="m-5 font-medium"> - </button>
                    <span className="px-4 py-1.5 border-2 rounded-lg border-gray-300">1</span>
                    <button className="m-5 font-medium"> + </button>
                </section>

                <section className="flex justify-items-center items-center">
                  <span className="">
                        ${(item.price * item.quantity).toFixed(2)}
                  </span>
                </section>
              </div>
            );
          })
        ) : (
          <div className="flex justify-center items-center flex-col pt-5">
            <h1 className="text-3xl mb-10 text-gray-400">
              Your cart is empty...
            </h1>
            <img src="/images/empty-cart.png" alt="cart" className=" w-[45%]" />
          </div>
        )
      ) : (
        <Spinner />
      )}
    </div>
  );
};
