import React from 'react'
import { Summary } from '../Summary'

type Props = {
    order: any[],
}

export const OrderSummary = (props: Props) => {
  return (
    <Summary>
        <div className='h-[400px] '>
            <h1 className="font-medium py-5">Order Summary</h1>

            <section className='px-5'>
                <ul className="list-decimal">
                    {props.order.length >= 0 ? props.order.map(item => {
                        return(
                            <li className='mb-2' key={item.id} >{item.name.length > 20 ? `${item.name.slice(0, 30)}...` : item.name}</li>
                        )
                    }) : null}
                </ul>
                    
            </section>

            <section className="flex py-2 px-5 justify-between bg-green-400 rounded-xl my-5 cursor-pointer hover:bg-green-300">
            <span>$ 20.00</span>
            <p>
              checkout <i className="fa-solid fa-arrow-right-long ml-3"></i>
            </p>
          </section>
        </div>
    </Summary>
  )
}
