import { cartConstant } from "../../constant/cartConstant";

export const cartReducer = (state = {cart: [], cartLoading: true, quantity: 0, id: null}, action:any) => {
    
    switch (action.type) {

            case cartConstant.REQUEST_CART:
                
                return { 
                    cartLoading: true, 
                    cart: null
                    }

            case cartConstant.GET_CART:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                    }

            case cartConstant.ADD_PRODUCT:
                
                return { 
                    cartLoading: true, 
                    cart: action.payload 
                    }

            case cartConstant.SUCCESS_ADD:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                    }

            case cartConstant.DELETE_PRODUCT:
                
                return { 
                    cartLoading: false, 
                    cart: action.payload
                }
    
            case cartConstant.ADD_QUANTITY:

                const { cart } = action;
                console.log(cart)
                const item = state.cart.find((product: any) => product.id === cart.id)

                console.log(item)

                return {
                    cart
                }


            case cartConstant.DROP_QUANTITY:
                return { 
                    cartLoading: false, 
                    error: action.payload
                }
                
            default:
                return state;
        }



}
