import React, { useEffect, useState } from 'react';
import { useSelector,RootStateOrAny } from 'react-redux';
import { Spinner } from '../components/Spinner/Spinner' 
import { PersonalInformation } from '../components/Profile/PersonalInformation'


type Props = {};

export const Profile = (props: Props) => {


  const userDetail = useSelector((state: RootStateOrAny) => state.loginReducer);
  const { userInfo, loading } = userDetail;

  const [editMode, setEditMode] = useState<boolean>(false);
  const [tabs, setTabs] = useState<number>(1);

  const [firstName, setFirstName] = useState<string>('')
  const [lastName, setLastName] = useState<string>('')
  const [email, setEmail] = useState<string>('')
  const [phoneNumber, setPhoneNumber] = useState<string>('')

  useEffect(() => {

    if (!loading) {

      setFirstName(userInfo.firstName)
      setLastName(userInfo.lastName)
      setEmail(userInfo.email)
      setPhoneNumber(userInfo.phoneNumber)
    }

  }, [loading])


  
  return <div>

    {loading 
    ? <Spinner />
    : (
      <PersonalInformation tabs={tabs} setTabs={setTabs} firstName={firstName} lastName={lastName} email={email} phoneNumber={phoneNumber} editMode={editMode} userInfo={userInfo} />
    )
  
}
   
  </div>
}
