import axios from "axios";
import { updateUserConstant } from "../constant/updateUserConstant";



export const updateUserAction = (id: string, firstName: string, lastName: string, email: string, phoneNumber: string) => async (dispatch: any) => {

    try {
        //console.log(email, password);
        dispatch({type: updateUserConstant.REQUEST_UPDATE})
        const user = await axios.post(`http://localhost:5000/api/updateUser`, {id, firstName, lastName, email, phoneNumber}, {withCredentials: true})
        
        dispatch({
            type: updateUserConstant.SUCCESS_UPDATE,
            payload: user.data
        })

    } catch (error: any) {
        console.log(error.response.data.message);
        dispatch({
            type: updateUserConstant.FAIL_UPDATE,
            payload: error.response.data.message
        })
    }
}
