import React, { useEffect, useState } from 'react'
import { setInterval } from 'timers/promises'

const carouselImage = [
    '/images/banner1.png',
    '/images/banner2.png',
    '/images/banner3.png'
]

let count = 0

export const Carousel = () => {

  const [currentTab, setCurrentTab] = useState(0)

  useEffect(() => {
    startSlider()
  }, []);

  const startSlider = () => {
    window.setInterval(() => {
      carouselNextHandler()
    }, 5000)
  }

  const carouselNextHandler = () => {
    count = (count + 1) % carouselImage.length
    setCurrentTab(count)
  }

  const carouselPreviousHandler = () => {
    count = (currentTab + carouselImage.length - 1) % carouselImage.length
    setCurrentTab(count)
  }

  return (
    <div className="max-w-screen-xl m-auto mb-10">
      <div className="w-full relative select-none">
        <img src={carouselImage[count]} alt="" className=' object-contain' />

        <div className="absolute top-0 bottom-0 w-full flex justify-between items-start px-3 ">
          <button onClick={carouselNextHandler} className='focus:outline-none'>Previous</button>
          <button onClick={carouselPreviousHandler} className='focus:outline-none'>Next</button>
        </div>
      </div>
    </div>
  )
}
