import React from 'react'
import { useState } from 'react'

type Props = {}

export const Purchase = (props: Props) => {

    const [currentTab, setCurrentTab] = useState<number>(0);

    const tabChangeHandler = (number: number)  => (e: React.MouseEvent) => {
        setCurrentTab(number)
    }

    const isTabActive = (number: number) => {
        if (number === currentTab) {
            return (
                'bg-blue-500'
            )
        } else {
            return 'bg-gray-300'
        }

    }


  return (
    <div className='pl-10'>
        <div className='flex items-center pt-5'>

            <section className={`h-[80px] w-[80px]  rounded-full flex justify-center items-center ${isTabActive(0)}`}>
                <span className='text-white'>
                    <i className="fa-solid fa-dollar-sign fa-xl"></i>
                </span>
            </section>
            <span className={`w-[120px] h-[1.8px] ${isTabActive(0)}`}></span>

            <section className='h-[80px] w-[80px]  rounded-full bg-gray-300 flex justify-center items-center' onClick={tabChangeHandler(1)}>
                <span className='text-white'>
                    <i className="fa-solid fa-box-open fa-xl"></i>
                </span>
            </section>
            
            <span className='w-[120px] h-[1.8px] bg-gray-300'></span>

            <section className='h-[80px] w-[80px] rounded-full bg-gray-300 flex justify-center items-center' onClick={tabChangeHandler(2)}>
                <span className='text-white'>
                    <i className="fa-solid fa-truck fa-xl"></i>
                </span>
            </section>
            
            <span className='w-[120px] h-[1.8px] bg-gray-300'></span>

            <section className='h-[80px] w-[80px] rounded-full bg-gray-300 flex justify-center items-center ' onClick={tabChangeHandler(3)}>
                <span className='text-white'>
                    <i className="fa-solid fa-check fa-xl"></i>
                </span>
            </section>
            
        </div>


    </div>
  )
}
