import React, { useEffect, useState } from "react";
import { RootStateOrAny, useSelector } from "react-redux";
import { Input } from "../components/Input";
import { ShippingSummary } from "../components/summary/shipping-summary/ShippingSummary";

type Props = {};

export const Shipping = (props: Props) => {

  const userDetail = useSelector((state: RootStateOrAny) => state.loginReducer);
  const { userInfo } = userDetail;
  
  const [option, setOption] = useState<number>(0);
  const [userAddress, setUserAddress] = useState<any>()

  const [fullName, setFullName] = useState<string>(userInfo && userAddress ? userAddress[option].fullName : '');
  const [phoneNumber, setPhoneNumber] = useState<number>(userAddress ? userAddress[option].fullName : '');
  const [address, setAddress] = useState<string>(userAddress ? userAddress[option].fullName : '');
  const [state, setState] = useState<string>(userAddress ? userAddress[option].fullName : '');


  const selectOptionHandler = (e: any) => {
    setOption(e.target.value)
  } 

  useEffect(() => {
    if (!userInfo) return
    setUserAddress(userInfo.address)
  }, [userInfo])
  
  return (
    <div className="max-w-7xl px-10 m-auto flex justify-between pb-10">
      <div className="w-[60%]">
        <h1 className="font-medium mt-5">Shipping details</h1>
        <section className="flex justify-between mt-5 items-center">
          <p className="text-sm">Use saved address</p>

          <select
            name=""
            id=""
            className="bg-blue-50 border-[1.5px] text-gray-400 rounded-md p-2 focus:outline-none border-blue-50"
            onChange={(e) => selectOptionHandler(e)} >
            {console.log(option)}
            {userInfo && userInfo.address.length > 0 ? (userInfo.address.map((item: any, index: number) => {
              return (
                <option key={index} value={index}>{`Address ${index + 1}`}</option>
              )
            })) : null}
          </select>
        </section>

        <section className="flex justify-between w-full gap-3">
          <Input
            value={fullName}
            setValue={setFullName}
            placeholder="Full Name"
          />
          <Input
            value={phoneNumber}
            setValue={setPhoneNumber}
            placeholder="Phone Number"
          />
        </section>
        <section className="">
          <Input value={state} setValue={setState} placeholder="State" />
        </section>

        <section>
          <textarea
            name=""
            id=""
            cols={30}
            rows={6}
            placeholder="Address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            className="w-full bg-blue-50 mt-5 rounded-lg px-3 py-1 focus:outline-none"
          />
        </section>

        <section className="flex justify-end mt-5">
          <button className=" mt-2 px-9 py-2 max-w-sm rounded-lg border-2 hover:bg-gray-300 focus:outline-none border-white ">
            Cancel
          </button>

          <button className=" mt-2 px-9 py-2 max-w-sm bg-blue-500 rounded-lg text-white hover:bg-blue-400 focus:outline-none ml-5">
            Confirm
          </button>
        </section>
      </div>

      <div className="w-[35%] mt-5">
        <ShippingSummary />
      </div>
    </div>
  );
};
